import axios from 'axios';

const apiUrl = "http://localhost:5062"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post(`${apiUrl}/items/{}`);
    return result.data;
  },

  setCompleted: async(id, name, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result = await axios.put(`${apiUrl}/items/${id}`,{"id":id,"name":name,"isComplete":isComplete})
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/items/${id}`)
    return result.data;
  }
};
